def calculator(array):
    print(np.mean(array))
    print("asd")
    for i in range(0, len(array)):
        mean = np.mean(array[i])
        print(mean)
    print("done")
    for i in range(0, len(array)):
        dv = np.std(array[i])
        de = stats.sem((array[i]))
        print(de)


